import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ExpenseList from '../components/ExpenseList';

const Dashboard = () => {
    const [expenses, setExpenses] = useState<any[]>([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    // Helper to get current NZT date/time as ISO string
    const getNZTDateTime = () => {
        const now = new Date();
        // NZT is UTC+12 or UTC+13 (with DST), so use Pacific/Auckland
        return new Date(now.toLocaleString('en-US', { timeZone: 'Pacific/Auckland' })).toISOString();
    };

    const [date, setDate] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toISOString().slice(0, 10); // yyyy-mm-dd
    });
    const [time, setTime] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toTimeString().slice(0, 5); // hh:mm
    });

    // Helper to get NZT date string (yyyy-mm-dd) from ISO
    const getNZTDayString = (iso: string) => {
        const d = new Date(iso);
        return d.toLocaleDateString('en-NZ', { timeZone: 'Pacific/Auckland' });
    };

    // Group expenses by day
    const groupExpensesByDay = (expenses: any[]) => {
        const grouped: { [day: string]: any[] } = {};
        expenses.forEach(exp => {
            const day = getNZTDayString(exp.date);
            if (!grouped[day]) grouped[day] = [];
            grouped[day].push(exp);
        });
        return grouped;
    };

    const [selectedDay, setSelectedDay] = useState<string>('');

    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    alert('You must be logged in to view your expenses.');
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/expenses', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // Map _id to id for compatibility with ExpenseList
                const expensesWithId = response.data.map((expense: any) => ({
                    ...expense,
                    id: expense._id,
                }));

                const sortedExpenses = expensesWithId.sort(
                    (a: any, b: any) => new Date(b.date).getTime() - new Date(a.date).getTime()
                );

                setExpenses(sortedExpenses);
                // Set initial selected day to most recent expense day
                if (sortedExpenses.length > 0) {
                    const mostRecentDay = getNZTDayString(sortedExpenses[0].date);
                    setSelectedDay(mostRecentDay);
                }
            } catch (err) {
                console.error('Error fetching expenses:', err);
                alert('Failed to fetch expenses. Please try again.');
            }
        };
        fetchExpenses();
    }, []);
    const handleEditExpense = async (id: string, updatedFields: any) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to edit an expense.');
                return;
            }

            const response = await axios.put(
                `http://localhost:5000/api/expenses/${id}`,
                updatedFields,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setExpenses(expenses => expenses.map(exp => exp.id === id ? { ...exp, ...response.data, id: exp.id } : exp));
            alert('Expense updated successfully!');
        } catch (err) {
            console.error('Error updating expense:', err);
            alert('Failed to update expense. Please try again.');
        }
    };

    const handleDeleteExpense = async (id: string) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to delete an expense.');
                return;
            }

            await axios.delete(`http://localhost:5000/api/expenses/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setExpenses(expenses.filter((expense: any) => expense.id !== id));
            alert('Expense deleted successfully!');
        } catch (err) {
            console.error('Error deleting expense:', err);
            alert('Failed to delete expense. Please try again.');
        }
    };

    const handleAddExpense = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to add an expense.');
                return;
            }

            // Combine date and time to ISO string in NZT
            const isoDate = new Date(`${date}T${time}:00+12:00`).toISOString();

            const response = await axios.post(
                'http://localhost:5000/api/expenses',
                { title, description, amount: parseFloat(amount), date: isoDate },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setExpenses([response.data, ...expenses]);
            setTitle('');
            setDescription('');
            setAmount('');
            // Reset to current NZT date/time
            const now = new Date(getNZTDateTime());
            setDate(now.toISOString().slice(0, 10));
            setTime(now.toTimeString().slice(0, 5));
            alert('Expense added successfully!');
        } catch (err) {
            console.error('Error adding expense:', err);
            alert('Failed to add expense. Please try again.');
        }
    };

    const formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
        };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    // Prepare grouped expenses and sorted days
    const groupedExpenses = groupExpensesByDay(expenses);
    const days = Object.keys(groupedExpenses).sort((a, b) => new Date(b).getTime() - new Date(a).getTime());
    const currentExpenses = groupedExpenses[selectedDay] || [];

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Dashboard</h1>
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <form className="add-expense-form" onSubmit={handleAddExpense}>
                        {/* ...existing code... */}
                        <div className="form-group">
                            <label>Title</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Description</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter description"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Amount</label>
                            <input
                                type="number"
                                className="form-control"
                                placeholder="Enter amount"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                        </div>
                        <div className="form-group d-flex gap-2 align-items-end">
                            <div style={{ flex: 1 }}>
                                <label>Date (NZT)</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={date}
                                    onChange={e => setDate(e.target.value)}
                                />
                            </div>
                            <div style={{ flex: 1 }}>
                                <label>Time (NZT)</label>
                                <input
                                    type="time"
                                    className="form-control"
                                    value={time}
                                    onChange={e => setTime(e.target.value)}
                                />
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block">
                            Add Expense
                        </button>
                    </form>
                </div>
            </div>
            <div className="d-flex justify-content-center align-items-center mt-4 mb-2 gap-2">
                <button
                    className="btn btn-secondary"
                    disabled={days.indexOf(selectedDay) === days.length - 1}
                    onClick={() => setSelectedDay(days[days.indexOf(selectedDay) + 1])}
                >
                    Previous Day
                </button>
                <span className="fw-bold">{selectedDay}</span>
                <button
                    className="btn btn-secondary"
                    disabled={days.indexOf(selectedDay) === 0}
                    onClick={() => setSelectedDay(days[days.indexOf(selectedDay) - 1])}
                >
                    Next Day
                </button>
            </div>
            <h2 className="text-center mt-3">Expenses for {selectedDay}</h2>
            <ExpenseList expenses={currentExpenses} onDelete={handleDeleteExpense} onEdit={handleEditExpense} />
        </div>
    );
};

export default Dashboard;
