    // Fetch expenses from backend
    const fetchExpenses = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setFetchError('You must be logged in to view expenses.');
                return;
            }
            const response = await axios.get('http://localhost:5000/api/expenses', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setExpenses(response.data);
            setFetchError(null);
        } catch (err) {
            setFetchError('Failed to fetch expenses. Please try again.');
        }
    };

    // Edit handler: update expense and refresh list
    const handleEditExpense = async (id: string, updatedExpense: any) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to edit an expense.');
                return;
            }
            await axios.put(`http://localhost:5000/api/expenses/${id}`, updatedExpense, {
                headers: { Authorization: `Bearer ${token}` },
            });
            await fetchExpenses();
            alert('Expense updated successfully!');
        } catch (err) {
            console.error('Error updating expense:', err);
            alert('Failed to update expense. Please try again.');
        }
    };
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ExpenseList from '../components/ExpenseList';

const Dashboard = () => {
    const [expenses, setExpenses] = useState<any[]>([]);

    // Error state for fetch
    const [fetchError, setFetchError] = useState<string | null>(null);
    // Fetch expenses from backend on mount
    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setFetchError('You must be logged in to view expenses.');
                    return;
                }
                const response = await axios.get('http://localhost:5000/api/expenses', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setExpenses(response.data);
                setFetchError(null);
            } catch (err) {
                console.error('Failed to fetch expenses:', err);
                setFetchError('Failed to fetch expenses. Please try again.');
            }
        };
        fetchExpenses();
    }, []);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    // Helper to get current NZT date/time as ISO string
    const getNZTDateTime = () => {
        const now = new Date();
        // NZT is UTC+12 or UTC+13 (with DST), so use Pacific/Auckland
        return new Date(now.toLocaleString('en-US', { timeZone: 'Pacific/Auckland' })).toISOString();
    };

    const [date, setDate] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toISOString().slice(0, 10); // yyyy-mm-dd
    });
    const [time, setTime] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toTimeString().slice(0, 5); // hh:mm
    });

    // Helper to get NZT date string (yyyy-mm-dd) from ISO
    const getNZTDayString = (iso: string) => {
        const d = new Date(iso);
        return d.toLocaleDateString('en-NZ', { timeZone: 'Pacific/Auckland' });
    };

    // Group expenses by day
    const groupExpensesByDay = (expenses: any[]) => {
        const grouped: { [day: string]: any[] } = {};
        expenses.forEach(exp => {
            const day = getNZTDayString(exp.date);
            if (!grouped[day]) grouped[day] = [];
            grouped[day].push(exp);
        });
        return grouped;
    };

    const handleDeleteExpense = async (id: string) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to delete an expense.');
                return;
            }

            await axios.delete(`http://localhost:5000/api/expenses/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            await fetchExpenses();
            alert('Expense deleted successfully!');
        } catch (err) {
            console.error('Error deleting expense:', err);
            alert('Failed to delete expense. Please try again.');
        }
    };

    const handleAddExpense = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to add an expense.');
                return;
            }

            // Combine date and time to ISO string in NZT
            const isoDate = new Date(`${date}T${time}:00+12:00`).toISOString();

            const response = await axios.post(
                'http://localhost:5000/api/expenses',
                { title, description, amount: parseFloat(amount), date: isoDate },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setExpenses([response.data, ...expenses]);
            setTitle('');
            setDescription('');
            setAmount('');
            // Reset to current NZT date/time
            const now = new Date(getNZTDateTime());
            setDate(now.toISOString().slice(0, 10));
            setTime(now.toTimeString().slice(0, 5));
            alert('Expense added successfully!');
        } catch (err) {
            console.error('Error adding expense:', err);
            alert('Failed to add expense. Please try again.');
        }
    };

    const formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
        };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    // Group and sort expenses by day
    const groupedExpenses = groupExpensesByDay(expenses);
    // Sort days newest to oldest
    const days = Object.keys(groupedExpenses).sort((a, b) => new Date(b).getTime() - new Date(a).getTime());
    const [selectedDay, setSelectedDay] = useState(days.length > 0 ? days[0] : '');
    const currentExpenses = groupedExpenses[selectedDay] || [];

    // Calculate total spend for the selected day
    const totalSpend = currentExpenses.reduce((sum, exp) => sum + Number(exp.amount || 0), 0);
    const dailyBudget = 100; // You can change this value

    // ...existing code...

    // ...existing code...
    

    // Ref for Flatpickr input
    const flatpickrRef = useRef<HTMLInputElement>(null);

    // Inject Flatpickr script on mount
    useEffect(() => {
        if (window && (window as any).flatpickr && flatpickrRef.current) {
            // Convert NZ format (D/M/YYYY) to YYYY-MM-DD for Flatpickr
            const allowedDates = days.map(day => {
                const parts = day.split('/');
                if (parts.length === 3) {
                    // Pad day and month to 2 digits
                    const dd = parts[0].padStart(2, '0');
                    const mm = parts[1].padStart(2, '0');
                    return `${parts[2]}-${mm}-${dd}`;
                }
                return null;
            }).filter(Boolean);
            (window as any).flatpickr(flatpickrRef.current, {
                dateFormat: 'Y-m-d',
                enable: allowedDates,
                onChange: function(selectedDates: Date[]) {
                    if (selectedDates.length > 0) {
                        const d = selectedDates[0];
                        // Pad day and month to 2 digits for NZ format
                        const dayStr = String(d.getDate()).padStart(2, '0');
                        const monthStr = String(d.getMonth() + 1).padStart(2, '0');
                        const formatted = `${dayStr}/${monthStr}/${d.getFullYear()}`;
                        if (days.includes(formatted)) {
                            setSelectedDay(formatted);
                        } else {
                            alert('No expenses for that day.');
                        }
                    }
                },
            });
        }
    }, [days]);

    // Update selectedDay when expenses change (e.g., after add/delete)
    useEffect(() => {
        if (days.length > 0) {
            // If current selectedDay is gone, pick the first day
            if (!days.includes(selectedDay)) {
                setSelectedDay(days[0]);
            }
        } else {
            setSelectedDay('');
        }
    }, [expenses]);

    // Income state
    const [incomeAmount, setIncomeAmount] = useState('');
    const [incomeFrequency, setIncomeFrequency] = useState('weekly');
    const [incomeLoading, setIncomeLoading] = useState(false);
    const [incomeError, setIncomeError] = useState('');
    const [showIncomeInput, setShowIncomeInput] = useState(false);
    const [editingIncome, setEditingIncome] = useState(false);
    const [blurIncome, setBlurIncome] = useState(false);

    // Calculate percent of income used and remaining income
    const income = parseFloat(incomeAmount) || 0;
    const percentIncomeUsed = income > 0 ? Math.min(100, Math.round((totalSpend / income) * 100)) : 0;
    const incomeLeft = income - totalSpend;
    // For backward compatibility, keep percent for daily budget
    const percent = percentIncomeUsed;

    // Fetch income on mount
    useEffect(() => {
        const fetchIncome = async () => {
            setIncomeLoading(true);
            setIncomeError('');
            try {
                const token = localStorage.getItem('token');
                if (!token) return;
                const response = await axios.get('http://localhost:5000/api/income', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                if (response.data) {
                    setIncomeAmount(response.data.amount?.toString() || '');
                    setIncomeFrequency(response.data.frequency || 'weekly');
                }
            } catch (err) {
                setIncomeError('Failed to fetch income.');
            } finally {
                setIncomeLoading(false);
            }
        };
        fetchIncome();
    }, []);

    // Save income handler
    const handleSaveIncome = async (e: React.FormEvent) => {
        e.preventDefault();
        setIncomeLoading(true);
        setIncomeError('');
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setIncomeError('You must be logged in to save income.');
                setIncomeLoading(false);
                return;
            }
            const response = await axios.post('http://localhost:5000/api/income', {
                amount: parseFloat(incomeAmount),
                frequency: incomeFrequency,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setIncomeAmount(response.data.amount?.toString() || '');
            setIncomeFrequency(response.data.frequency || 'weekly');
            setEditingIncome(false);
            setShowIncomeInput(false);
        } catch (err) {
            setIncomeError('Failed to save income.');
        } finally {
            setIncomeLoading(false);
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Dashboard</h1>
            <div className="row g-4 align-items-stretch">
                <div className="col-12 col-md-6 d-flex">
                    <form className="add-expense-form flex-fill" onSubmit={handleAddExpense}>
                        <div className="form-group">
                            <label>Title</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Description</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter description"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Amount</label>
                            <input
                                type="number"
                                className="form-control"
                                placeholder="Enter amount"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                        </div>
                        <div className="form-group d-flex gap-2 align-items-end">
                            <div style={{ flex: 1 }}>
                                <label>Date (NZT)</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={date}
                                    onChange={e => setDate(e.target.value)}
                                />
                            </div>
                            <div style={{ flex: 1 }}>
                                <label>Time (NZT)</label>
                                <input
                                    type="time"
                                    className="form-control"
                                    value={time}
                                    onChange={e => setTime(e.target.value)}
                                />
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block">
                            Add Expense
                        </button>
                    </form>
                </div>
                <div className="col-12 col-md-6 d-flex flex-column gap-3">
                    {/* Combined Total Spend & Income Card */}
                    <div className="card shadow-sm total-spend-card flex-fill d-flex flex-column justify-content-center">
                        <div className="card-body">
                            <div className="d-flex flex-column flex-md-row align-items-md-center justify-content-between mb-3 gap-3">
                                <div className="text-center text-md-start flex-fill">
                                    <h5 className="card-title mb-2">Total Spend for {selectedDay}</h5>
                                    <h2 className="display-5 mb-3 total-spend-amount">
                                        ${totalSpend.toFixed(2)}
                                    </h2>
                                    <div className="progress animated-progress" style={{ height: 28, background: 'var(--progress-bg)' }}>
                                        <div
                                            className={`progress-bar total-spend-bar ${percent > 80 ? 'bg-danger' : percent > 50 ? 'bg-warning' : 'bg-success'}`}
                                            role="progressbar"
                                            style={{ width: `${percent}%`, fontWeight: 'bold', fontSize: 16, transition: 'width 1s cubic-bezier(.4,2,.3,1)' }}
                                            aria-valuenow={percent}
                                            aria-valuemin={0}
                                            aria-valuemax={100}
                                        >
                                            {percent}%
                                        </div>
                                    </div>
                                    <div className="mt-2 text-muted total-spend-budget" style={{ fontSize: 14 }}>
                                        Income: ${income.toFixed(2)}<br />
                                        Used: {percent}%<br />
                                        Left: ${incomeLeft.toFixed(2)}
                                    </div>
                                </div>
                                <div className="income-section ms-md-4 mt-4 mt-md-0" style={{ minWidth: 220 }}>
                                    <h6 className="mb-2">Income (after tax)</h6>
                                    {!showIncomeInput && !editingIncome && incomeAmount ? (
                                        <div className="d-flex align-items-center gap-2">
                                            <span style={{ fontWeight: 'bold', fontSize: 18, filter: blurIncome ? 'blur(6px)' : 'none', transition: 'filter 0.2s' }}>${parseFloat(incomeAmount).toFixed(2)}</span>
                                            <button className="btn btn-outline-primary btn-sm d-flex align-items-center" onClick={() => setEditingIncome(true)} title="Edit income">
                                                <span className="material-icons">edit</span>
                                            </button>
                                            <button className="btn btn-outline-secondary btn-sm d-flex align-items-center" onClick={() => setBlurIncome(b => !b)} title={blurIncome ? 'Show income' : 'Hide income'}>
                                                <span className="material-icons">{blurIncome ? 'visibility_off' : 'visibility'}</span>
                                            </button>
                                        </div>
                                    ) : (
                                        <form className="d-flex flex-column gap-2" onSubmit={handleSaveIncome}>
                                            <div className="input-group">
                                                <span className="input-group-text">$</span>
                                                <input
                                                    type="number"
                                                    className="form-control"
                                                    placeholder="Enter income"
                                                    min="0"
                                                    step="0.01"
                                                    value={incomeAmount}
                                                    onChange={e => setIncomeAmount(e.target.value)}
                                                    disabled={incomeLoading}
                                                />
                                            </div>
                                            <div className="d-flex gap-2 align-items-center">
                                                <label className="me-2 mb-0">Frequency:</label>
                                                <select
                                                    className="form-select"
                                                    style={{ maxWidth: 140 }}
                                                    value={incomeFrequency}
                                                    onChange={e => setIncomeFrequency(e.target.value)}
                                                    disabled={incomeLoading}
                                                >
                                                    <option value="weekly">Weekly</option>
                                                    <option value="fortnightly">Fortnightly</option>
                                                    <option value="monthly">Monthly</option>
                                                </select>
                                                <button type="submit" className="btn btn-success ms-2" disabled={incomeLoading}>
                                                    {incomeLoading ? 'Saving...' : (editingIncome ? 'Update' : 'Save')}
                                                </button>
                                                {(editingIncome || showIncomeInput) && (
                                                    <button type="button" className="btn btn-secondary ms-2" onClick={() => { setEditingIncome(false); setShowIncomeInput(false); }}>
                                                        Cancel
                                                    </button>
                                                )}
                                            </div>
                                            {incomeError && <div className="text-danger mt-1">{incomeError}</div>}
                                        </form>
                                    )}
                                    {!incomeAmount && !showIncomeInput && !editingIncome && (
                                        <button className="btn btn-outline-success mt-2" onClick={() => setShowIncomeInput(true)}>
                                            Enter Income
                                        </button>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            {fetchError && (
                <div className="alert alert-danger text-center mt-4">{fetchError}</div>
            )}
            {days.length > 0 && selectedDay && (
                <div className="d-flex justify-content-center align-items-center mt-4 mb-2 gap-2">
                    <button
                        className="btn btn-outline-secondary btn-round d-flex align-items-center justify-content-center"
                        disabled={days.indexOf(selectedDay) === 0}
                        onClick={() => setSelectedDay(days[days.indexOf(selectedDay) - 1])}
                        title="Previous Day (newer)"
                        style={{ width: 40, height: 40, borderRadius: '50%' }}
                    >
                        <span className="material-icons">chevron_left</span>
                    </button>
                    <span className="fw-bold d-flex align-items-center gap-2">
                        {selectedDay}
                        <input
                            type="text"
                            ref={flatpickrRef}
                            className="form-control ms-2"
                            style={{ maxWidth: 140 }}
                            placeholder="Select day"
                            readOnly
                        />
                    </span>
                    <button
                        className="btn btn-outline-secondary btn-round d-flex align-items-center justify-content-center"
                        disabled={days.indexOf(selectedDay) === days.length - 1}
                        onClick={() => setSelectedDay(days[days.indexOf(selectedDay) + 1])}
                        title="Next Day (older)"
                        style={{ width: 40, height: 40, borderRadius: '50%' }}
                    >
                        <span className="material-icons">chevron_right</span>
                    </button>
                </div>
            )}
            <h2 className="text-center mt-3">Expenses for {selectedDay}</h2>
            {expenses.length === 0 ? (
                <div className="text-center text-muted mt-4">No expenses found.</div>
            ) : currentExpenses.length === 0 ? (
                <div className="text-center text-muted mt-4">No expenses for this day.</div>
            ) : (
                <ExpenseList expenses={currentExpenses} onDelete={handleDeleteExpense} onEdit={handleEditExpense} />
            )}
        </div>
    );
};

export default Dashboard;
