import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button } from 'react-bootstrap';

interface Expense {
    _id: string;
    title: string;
    description: string;
    amount: number;
    date: string;
}

const Dashboard: React.FC = () => {
    const [expenses, setExpenses] = useState<Expense[]>([]);
    const [selectedExpense, setSelectedExpense] = useState<Expense | null>(null); // State for the selected expense
    const [showModal, setShowModal] = useState(false); // State to control the modal visibility

    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    alert('You must be logged in to view your expenses.');
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/expenses', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const sortedExpenses = response.data.sort(
                    (a: Expense, b: Expense) => new Date(b.date).getTime() - new Date(a.date).getTime()
                );

                setExpenses(sortedExpenses);
            } catch (err) {
                console.error('Error fetching expenses:', err);
                alert('Failed to fetch expenses. Please try again.');
            }
        };

        fetchExpenses();
    }, []);

    const handleExpenseClick = (expense: Expense) => {
        setSelectedExpense(expense); // Set the selected expense
        setShowModal(true); // Show the modal
    };

    const handleCloseModal = () => {
        setShowModal(false); // Hide the modal
        setSelectedExpense(null); // Clear the selected expense
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Dashboard</h1>
            <ul className="list-group">
                {expenses.map((expense) => (
                    <li
                        key={expense._id}
                        className="list-group-item d-flex justify-content-between align-items-center mb-3"
                        style={{ cursor: 'pointer' }}
                        onClick={() => handleExpenseClick(expense)} // Make the entry clickable
                    >
                        <div>
                            <strong>{expense.title}</strong>: ${expense.amount} on {expense.date}
                        </div>
                    </li>
                ))}
            </ul>

            {/* Modal for displaying expense details */}
            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Expense Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedExpense && (
                        <>
                            <p><strong>Title:</strong> {selectedExpense.title}</p>
                            <p><strong>Description:</strong> {selectedExpense.description}</p>
                            <p><strong>Amount:</strong> ${selectedExpense.amount}</p>
                            <p><strong>Date:</strong> {selectedExpense.date}</p>
                        </>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Dashboard;
