import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import ParticleBackground from './components/ParticleBackground';

const App: React.FC = () => {
    const isLoggedIn = !!localStorage.getItem('token');

    return (
        <>
            <ParticleBackground />
            <Router>
                <Header />
                <Switch>
                    <Route path="/login" component={Login} />
                    <Route
                        path="/dashboard"
                        render={() => (isLoggedIn ? <Dashboard /> : <Redirect to="/login" />)}
                    />
                    <Route path="/" exact>
                        <div className="home-hero-container" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '60vh', padding: '32px 0' }}>
                            <img src={require('../assets/main.png')} alt="Hero" style={{ maxWidth: '420px', width: '100%', borderRadius: '18px', boxShadow: '0 8px 32px rgba(31,38,135,0.18)' }} />
                            <h1 className="mt-4 mb-2" style={{ color: '#fff', fontWeight: 700, fontSize: '2.5rem', textShadow: '0 2px 8px rgba(0,0,0,0.18)' }}>
                                Welcome to Matty E's Budget App
                            </h1>
                            <p className="lead" style={{ color: '#e0e0e0', maxWidth: 480, textAlign: 'center', marginBottom: 0 }}>
                                Take control of your finances with a beautiful, modern dashboard and easy expense tracking.
                            </p>
                        </div>
                    </Route>
                </Switch>
                <Footer />
            </Router>
        </>
    );
};

export default App;
