import React, { useState, useEffect, forwardRef } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import ExpenseList from '../components/ExpenseList';

const Dashboard = () => {
    const [expenses, setExpenses] = useState<any[]>([]);

    // Error state for fetch
    const [fetchError, setFetchError] = useState<string | null>(null);
    // Fetch expenses from backend on mount
    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setFetchError('You must be logged in to view expenses.');
                    return;
                }
                const response = await axios.get('http://localhost:5000/api/expenses', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setExpenses(response.data);
                setFetchError(null);
            } catch (err) {
                console.error('Failed to fetch expenses:', err);
                setFetchError('Failed to fetch expenses. Please try again.');
            }
        };
        fetchExpenses();
    }, []);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    // Helper to get current NZT date/time as ISO string
    const getNZTDateTime = () => {
        const now = new Date();
        // NZT is UTC+12 or UTC+13 (with DST), so use Pacific/Auckland
        return new Date(now.toLocaleString('en-US', { timeZone: 'Pacific/Auckland' })).toISOString();
    };

    const [date, setDate] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toISOString().slice(0, 10); // yyyy-mm-dd
    });
    const [time, setTime] = useState(() => {
        const now = new Date(getNZTDateTime());
        return now.toTimeString().slice(0, 5); // hh:mm
    });

    // Helper to get NZT date string (yyyy-mm-dd) from ISO
    const getNZTDayString = (iso: string) => {
        const d = new Date(iso);
        return d.toLocaleDateString('en-NZ', { timeZone: 'Pacific/Auckland' });
    };

    // Group expenses by day
    const groupExpensesByDay = (expenses: any[]) => {
        const grouped: { [day: string]: any[] } = {};
        expenses.forEach(exp => {
            const day = getNZTDayString(exp.date);
            if (!grouped[day]) grouped[day] = [];
            grouped[day].push(exp);
        });
        return grouped;
    };

    const handleDeleteExpense = async (id: string) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to delete an expense.');
                return;
            }

            await axios.delete(`http://localhost:5000/api/expenses/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setExpenses(expenses.filter((expense: any) => expense.id !== id));
            alert('Expense deleted successfully!');
        } catch (err) {
            console.error('Error deleting expense:', err);
            alert('Failed to delete expense. Please try again.');
        }
    };

    const handleAddExpense = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to add an expense.');
                return;
            }

            // Combine date and time to ISO string in NZT
            const isoDate = new Date(`${date}T${time}:00+12:00`).toISOString();

            const response = await axios.post(
                'http://localhost:5000/api/expenses',
                { title, description, amount: parseFloat(amount), date: isoDate },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setExpenses([response.data, ...expenses]);
            setTitle('');
            setDescription('');
            setAmount('');
            // Reset to current NZT date/time
            const now = new Date(getNZTDateTime());
            setDate(now.toISOString().slice(0, 10));
            setTime(now.toTimeString().slice(0, 5));
            alert('Expense added successfully!');
        } catch (err) {
            console.error('Error adding expense:', err);
            alert('Failed to add expense. Please try again.');
        }
    };

    const formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
        };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    // Group and sort expenses by day
    const groupedExpenses = groupExpensesByDay(expenses);
    const days = Object.keys(groupedExpenses).sort((a, b) => new Date(b).getTime() - new Date(a).getTime());
    const [selectedDay, setSelectedDay] = useState(days.length > 0 ? days[0] : '');
    const currentExpenses = groupedExpenses[selectedDay] || [];

    // Update selectedDay when expenses change (e.g., after add/delete)
    useEffect(() => {
        if (days.length > 0) {
            // If current selectedDay is gone, pick the first day
            if (!days.includes(selectedDay)) {
                setSelectedDay(days[0]);
            }
        } else {
            setSelectedDay('');
        }
    }, [expenses]);

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Dashboard</h1>
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <form className="add-expense-form" onSubmit={handleAddExpense}>
                        <div className="form-group">
                            <label>Title</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Description</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Enter description"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Amount</label>
                            <input
                                type="number"
                                className="form-control"
                                placeholder="Enter amount"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                        </div>
                        <div className="form-group d-flex gap-2 align-items-end">
                            <div style={{ flex: 1 }}>
                                <label>Date (NZT)</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={date}
                                    onChange={e => setDate(e.target.value)}
                                />
                            </div>
                            <div style={{ flex: 1 }}>
                                <label>Time (NZT)</label>
                                <input
                                    type="time"
                                    className="form-control"
                                    value={time}
                                    onChange={e => setTime(e.target.value)}
                                />
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block">
                            Add Expense
                        </button>
                    </form>
                </div>
            </div>
            {fetchError && (
                <div className="alert alert-danger text-center mt-4">{fetchError}</div>
            )}
            {days.length > 0 && selectedDay && (
                <div className="d-flex justify-content-center align-items-center mt-4 mb-2 gap-2">
                    <button
                        className="btn btn-outline-secondary btn-round d-flex align-items-center justify-content-center"
                        disabled={days.indexOf(selectedDay) === days.length - 1}
                        onClick={() => setSelectedDay(days[days.indexOf(selectedDay) + 1])}
                        title="Previous Day"
                        style={{ width: 40, height: 40, borderRadius: '50%' }}
                    >
                        <span className="material-icons">chevron_left</span>
                    </button>
                    <span className="fw-bold d-flex align-items-center gap-2">
                        {selectedDay}
                        <DatePicker
                            selected={(() => {
                                const parts = selectedDay.split('/');
                                if (parts.length === 3) {
                                    return new Date(`${parts[2]}-${parts[1]}-${parts[0]}`);
                                }
                                return null;
                            })()}
                            onChange={date => {
                                if (!date) return;
                                const formatted = date.toLocaleDateString('en-NZ', { timeZone: 'Pacific/Auckland' });
                                if (days.includes(formatted)) {
                                    setSelectedDay(formatted);
                                } else {
                                    alert('No expenses for that day.');
                                }
                            }}
                            dateFormat="dd/MM/yyyy"
                            className="form-control"
                            includeDates={days
                                .map(day => {
                                    const parts = day.split('/');
                                    if (parts.length === 3) {
                                        return new Date(`${parts[2]}-${parts[1]}-${parts[0]}`);
                                    }
                                    return null;
                                })
                                .filter((d): d is Date => d instanceof Date && !isNaN(d.getTime()))}
                            customInput={
                                <button
                                    className="btn btn-outline-primary btn-round d-flex align-items-center justify-content-center"
                                    title="Select Day"
                                    style={{ width: 40, height: 40, borderRadius: '50%' }}
                                >
                                    <span className="material-icons">calendar_today</span>
                                </button>
                            }
                            popperPlacement="bottom"
                        />
                    </span>
                    <button
                        className="btn btn-outline-secondary btn-round d-flex align-items-center justify-content-center"
                        disabled={days.indexOf(selectedDay) === 0}
                        onClick={() => setSelectedDay(days[days.indexOf(selectedDay) - 1])}
                        title="Next Day"
                        style={{ width: 40, height: 40, borderRadius: '50%' }}
                    >
                        <span className="material-icons">chevron_right</span>
                    </button>
                </div>
            )}
            <h2 className="text-center mt-3">Expenses for {selectedDay}</h2>
            <ExpenseList expenses={currentExpenses} onDelete={handleDeleteExpense} onEdit={handleEditExpense} />
        </div>
    );
};

export default Dashboard;
