import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Accordion } from 'react-bootstrap';

interface Expense {
    _id: string;
    title: string;
    description: string;
    amount: number;
    date: string;
}

const Dashboard: React.FC = () => {
    const [expenses, setExpenses] = useState<Expense[]>([]);

    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    alert('You must be logged in to view your expenses.');
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/expenses', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const sortedExpenses = response.data.sort(
                    (a: Expense, b: Expense) => new Date(b.date).getTime() - new Date(a.date).getTime()
                );

                setExpenses(sortedExpenses);
            } catch (err) {
                console.error('Error fetching expenses:', err);
                alert('Failed to fetch expenses. Please try again.');
            }
        };

        fetchExpenses();
    }, []);

    const formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
        };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Dashboard</h1>
            <Accordion>
                {expenses.map((expense, index) => (
                    <Accordion.Item eventKey={index.toString()} key={expense._id}>
                        <Accordion.Header>
                            <strong>{expense.title}</strong> - ${expense.amount} on {formatDate(expense.date)}
                        </Accordion.Header>
                        <Accordion.Body>
                            <p><strong>Description:</strong> {expense.description || 'No description provided'}</p>
                            <p><strong>Amount:</strong> ${expense.amount}</p>
                            <p><strong>Date:</strong> {formatDate(expense.date)}</p>
                        </Accordion.Body>
                    </Accordion.Item>
                ))}
            </Accordion>
        </div>
    );
};

export default Dashboard;
